import {iter} from '@iterable-iterator/iter';
import {list} from '@iterable-iterator/list';
import {_next} from '@iterable-iterator/next';
import {map} from '@iterable-iterator/map';

/**
 * Zips iterables together. Yields a tuple containing the first element of each
 * iterable, then a tupe containing the second element of each iterable, etc.
 * Stops when one of the iterables runs out of elements.
 *
 * @example
 * // returns [ [ 'a' , 1 ] , [ 'b' , 2 ] , [ 'c' , 3 ] ]
 * list( _zip( [ 'abcd' , range(3) ] ) ) ;
 *
 * @param {Iterable[]} iterables - The iterables to zip.
 * @returns {IterableIterator}
 *
 */
export default function* _zip(iterables) {
	const iterators = list(map(iter, iterables));

	if (iterators.length === 0) {
		return;
	}

	while (true) {
		const buffer = [];

		for (const result of map(_next, iterators)) {
			if (result.done) {
				return;
			}

			buffer.push(result.value);
		}

		yield buffer;
	}
}

import {iter} from '@iterable-iterator/iter';

/**
 * Zips exactly two iterables together. Yields a tuple containing the first
 * element of each iterable, then a tupe containing the second element of each
 * iterable, etc.  Stops when one of the two iterables runs out of elements.
 *
 * @example
 * // returns [ [ 'a' , 1 ] , [ 'b' , 2 ] , [ 'c' , 3 ] ]
 * list( _zip2( 'abcd' , range(1, 4) ) ) ;
 *
 * @param {Iterable} A - The first iterable.
 * @param {Iterable} B - The second iterable.
 * @returns {IterableIterator}
 *
 */
export default function* _zip2(A, B) {
	const iA = iter(A);
	const iB = iter(B);

	while (true) {
		const a = iA.next();
		if (a.done) {
			return;
		}

		const b = iB.next();
		if (b.done) {
			return;
		}

		yield [a.value, b.value];
	}
}

import {iter} from '@iterable-iterator/iter';
import {list} from '@iterable-iterator/list';
import {_next} from '@iterable-iterator/next';
import {map} from '@iterable-iterator/map';
import {repeat} from '@iterable-iterator/repeat';
import enumerate from './enumerate.js';

/**
 * Same as _zip, but continues to yield zipped tuples until the last iterable is
 * exhausted.
 *
 * @example
 * // returns [['A','x'],['B','y'],['C','-'],['D','-']]
 * list( _ziplongest( '-' , [ 'ABCD', 'xy' ] ) ) ;
 *
 * @param {any} fillvalue - The value to yield for iterators that are exhausted.
 * @param {Iterable[]} iterables - The iterables to zip.
 * @returns {IterableIterator}
 *
 */
export default function* _ziplongest(fillvalue, iterables) {
	let counter = iterables.length;

	if (counter === 0) {
		return;
	}

	const filler = repeat(fillvalue);

	const iterators = list(map(iter, iterables));

	while (true) {
		const buffer = [];

		for (const [i, event] of enumerate(map(_next, iterators))) {
			if (event.done) {
				--counter;
				if (counter === 0) {
					return;
				}

				iterators[i] = filler;
				buffer.push(fillvalue);
			} else {
				buffer.push(event.value);
			}
		}

		yield buffer;
	}
}

import {_count} from '@iterable-iterator/count';
import _zip2 from './_zip2.js';

/**
 * Yields (index,element) tuples where the elements are taken from the input
 * iterable. You can choose the starting index. The starting index is
 * <code>0</code> by default.
 *
 * @example
 * // returns [ [ 0 , 'a' ] , [ 1 , 'b' ] , [ 2 , 'c' ] ]
 * list( enumerate( 'abc' ) ) ;
 *
 * @example
 * // returns [ [ 1 , 'a' ] , [ 2 , 'b' ] , [ 3 , 'c' ] ]
 * list( enumerate( 'abc' , 1 ) ) ;
 *
 * @param {Iterable} iterable - The input iterable.
 * @param {number} [start=0] - The starting index.
 * @returns {IterableIterator}
 */
export default function enumerate(iterable, start = 0) {
	return _zip2(_count(start, 1), iterable);
}

